import React, { useState } from 'react';
import {FaStar} from "react-icons/fa";

const Rating = (value) => {
    const [rating, setRating] = useState(null); //state hook 
    const [hover, setHover] = useState(null); //state hook 
    // console.log(setRating);
    // console.log(rating);
    return ( 
        <div>
            {[...Array(5)].map((star,i)=>{
                const ratingval = i+1;
                return (
                    <label key={ratingval}>
                        <input
                            type="radio" 
                            name="rating" 
                            value={ratingval} 
                            onClick={()=>(setRating(ratingval))}
                        />
                        <FaStar
                            className="star" 
                            color={ratingval <= (hover || rating) ? "#ffc107" : "e4e5e9"} 
                            size={50}
                            onMouseEnter={()=>setHover(ratingval)}
                            onMouseLeave={()=>setHover(null)}
                        />
                    </label>
                );
            })}
        </div>
    );
}
export default Rating;
