// import React, { Component } from 'react';
import React,{useState,useEffect} from 'react';
import CardList from '../components/CardList';
import SearchBox from '../components/SearchBox';
import Scroll from '../components/Scroll';
import './App.css';
// import Rating from '../components/Rating';
import db from './firebase.config.js';
import axios from 'axios';

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [data, setData] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const handleChange = event => {
    setSearchTerm(event.target.value);
  };
  var arr = [];
  const fetchRatings=async()=>{
    const response=db.collection('ratings');
    console.log("incomfin response", response);
    const data=await response.get();
    data.docs.forEach(item=>{
     setRatings(ratings=> [...ratings,item.data()]);
    });
    // console.log("ratings:", data[0].keys())
  }

  console.log("arrr::::",arr);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        'https://jsonplaceholder.typicode.com/users',
      );
      console.log(result.data);

      setData(result.data);
      setSearchResults(result.data)
    };

    fetchData();
  }, []);

  useEffect(() => {
    fetchRatings();
    const results = data.filter(robot =>
      robot.name.toLowerCase().includes(searchTerm)
    );
    setSearchResults(results);
  }, [searchTerm]);

  return (
    <div>
      <div className='tc'>
        <h1 className='f1'>RoboFriends</h1>
        {/* <SearchBox searchChange={handleChange}/> */}
        <div className='pa2'>
          <input
            className='pa3 ba b--green bg-lightest-blue'
            type='text'
            placeholder='search robots'
            value={searchTerm}
            onChange={handleChange}
          />
      </div>
        <Scroll>
          <CardList robots={searchResults} ratings={ratings}/>
        </Scroll>
      </div>
    </div>
  );

}

export default App;
